// –í–∏—Ö—ñ–¥: [{ json: { keywords: [...] } }]

function toArray(x) {
  if (!x) return [];
  if (Array.isArray(x)) return x;
  if (typeof x === 'string') {
    // —Å–ø—Ä–æ–±—É—î–º–æ —Ä–æ–∑–ø–∞—Ä—Å–∏—Ç–∏ JSON-—Ç–µ–∫—Å—Ç
    try {
      const parsed = JSON.parse(x);
      if (Array.isArray(parsed)) return parsed;
      if (parsed && Array.isArray(parsed.best_keywords)) {
        return parsed.best_keywords.map(o => o.keyword ?? o);
      }
      if (Array.isArray(parsed.keywords)) return parsed.keywords;
    } catch (_) {}
    // –∞–±–æ —Ä–æ–∑–±–∏—Ç–∏ –ø–æ —Ä—è–¥–∫–∞—Ö/–∫–æ–º–∞—Ö
    return x.split(/\n|,/).map(s => s.trim()).filter(Boolean);
  }
  return [];
}

function normalizeLine(s) {
  return String(s)
    .replace(/^[-‚Ä¢*+>\d.\)\s]+/, '')        // –º–∞—Ä–∫–µ—Ä–∏ —Å–ø–∏—Å–∫—ñ–≤/–Ω—É–º–µ—Ä–∞—Ü—ñ—è
    .replace(/```/g, '')                    // code-fences
    .replace(/^["'`]+|["'`]+$/g, '')        // –ª–∞–ø–∫–∏/–±–µ–∫—Ç–∏–∫–∏ –ø–æ –∫—Ä–∞—è—Ö
    .replace(/^[\s,"'`\[\]]+/, '')          // –∑–∞–π–≤—ñ —Å–∏–º–≤–æ–ª–∏ –∑–ª—ñ–≤–∞
    .replace(/[\s,"'`\[\],]+$/, '')         // —ñ —Å–ø—Ä–∞–≤–∞ (–≤ —Ç.—á. –≤–∏—Å—è—á—ñ –∫–æ–º–∏/–¥—É–∂–∫–∏)
    .trim()
    .replace(/\s+/g, ' ');                  // –∑–ª–∏–ø–ª—ñ –ø—Ä–æ–±—ñ–ª–∏ -> –æ–¥–∏–Ω
}

const raw =
  $json.output ??
  $json.best_keywords ??
  $json.keywords ??
  $json;

let arr = toArray(raw);

// –¥–µ—è–∫—ñ –µ–ª–µ–º–µ–Ω—Ç–∏ –º–æ–∂—É—Ç—å –º—ñ—Å—Ç–∏—Ç–∏ "–∑–ª—ñ–ø–ª–µ–Ω—ñ" —Å–ø–∏—Å–∫–∏ ‚Äî –¥–æ–¥–∞—Ç–∫–æ–≤–æ —Ä–æ–∑—ñ–±‚Äô—î–º–æ
arr = arr.flatMap(v => {
  if (typeof v === 'string' && /,|\n/.test(v)) {
    return v.split(/\n|,/).map(s => s.trim()).filter(Boolean);
  }
  return [v];
});

const cleaned = [...new Set(
  arr
    .map(normalizeLine)
    // –ø—Ä–∏–±—Ä–∞—Ç–∏ —Å–ª—É–∂–±–æ–≤—ñ —Ä—è–¥–∫–∏ —Ç–∏–ø—É 'keywords: [' –∞–±–æ ']'
    .filter(s => !/^"?keywords?"?\s*:?\s*\[$/i.test(s))
    .filter(s => s !== ']' && s !== '[')
    // –∑–∞–≥–∞–ª—å–Ω—ñ —Ñ—ñ–ª—å—Ç—Ä–∏
    .filter(Boolean)
    .filter(s => s.toLowerCase() !== 'json')
    .filter(s => s !== '```')
    .filter(s => s.length >= 2 && s.length <= 80)
    .map(s => s.toLowerCase())             // —Å—Ç–∞–±—ñ–ª—ñ–∑—É—î–º–æ —Ä–µ–≥—ñ—Å—Ç—Ä
)]
// –≤—ñ–¥—Å—ñ–∫—Ç–∏ —è–≤–Ω—É –∫–æ–º–µ—Ä—Ü—ñ—é
.filter(s => !/(buy|price|for sale|near me|dealer|shop)/i.test(s))
.slice(0, 20); // –Ω–µ –±—ñ–ª—å—à–µ 20

return [{ json: { keywords: cleaned } }];

const pool = $flow.get('good_pool') ?? [];
for (const it of $items()) {
  const x = it.json;
  if (!x || !x.keyword) continue;
  pool.push({
    keyword: String(x.keyword).trim(),
    volume: x.volume ?? null,
    cpc: x.cpc ?? null,
    competition_index: x.competition_index ?? null,
    score: x.score ?? null,
  });
}

// –¥–µ–¥—É–ø –∑–∞ keyword (case-insensitive)
const seen = new Set();
const deduped = [];
for (const k of pool) {
  const key = k.keyword.toLowerCase();
  if (seen.has(key)) continue;
  seen.add(key);
  deduped.push(k);
}

$flow.set('good_pool', deduped);
return [{ json: { collected: deduped.length } }];

// Mode: Run Once For All Items
const src =
  $json?.result?.[0]?.items ??
  $json?.result ??
  $json?.tasks?.[0]?.result ??
  $json?.tasks ??
  [];

const out = [];
const seen = new Set();

for (const it of src) {
  const kw = (it.keyword || it?.keyword_data?.keyword || '').replace(/^"|"$/g, '').trim();
  if (!kw || seen.has(kw)) continue; seen.add(kw);

  const vol  = it.search_volume ?? it?.keyword_data?.search_volume ?? 0;
  const cpc  = it.cpc ?? it?.keyword_data?.cpc ?? 0;
  const comp = it.competition_index ?? it?.keyword_data?.competition_index ?? 0;
  const intent = String(it.search_intent || it?.keyword_properties?.search_intent || '').toLowerCase();

  // üëâ –¢–∏–º—á–∞—Å–æ–≤–æ –ø–æ–º‚Äô—è–∫—à–∏ —Ñ—ñ–ª—å—Ç—Ä, —â–æ–± –ø–µ—Ä–µ–≤—ñ—Ä–∏—Ç–∏ –ø–æ—Ç—ñ–∫
  if (vol < 100) continue;
  if (intent && !intent.includes('informational')) continue;

  const vinBoost   = /vin|compatib|fitment/i.test(kw) ? 500 : 0;
  const brandBoost = /\b(bmw|mercedes|toyota|honda|ford)\b/i.test(kw) ? 200 : 0;
  const trustBoost = /(oem|reliable|quality|inspection|warranty)/i.test(kw) ? 150 : 0;

  const score = vol * 0.7 + (100 - comp) * 2 + cpc * 10 + vinBoost + brandBoost + trustBoost;

  out.push({ keyword: kw, volume: vol, cpc, competition_index: comp, intent, score });
}

// –Ø–∫—â–æ –ø—ñ—Å–ª—è –≤—Å—å–æ–≥–æ –ø–æ—Ä–æ–∂–Ω—å–æ ‚Äî –∑—Ä–æ–±–∏–º–æ fallback: –≤—ñ–∑—å–º–µ–º–æ –≤—Ö—ñ–¥ —è–∫ —î (–±–µ–∑ intent-—Ñ—ñ–ª—å—Ç—Ä–∞)
if (out.length === 0 && Array.isArray(src)) {
  for (const it of src) {
    const kw = (it.keyword || it?.keyword_data?.keyword || '').replace(/^"|"$/g, '').trim();
    if (!kw) continue;
    const vol  = it.search_volume ?? it?.keyword_data?.search_volume ?? 0;
    const cpc  = it.cpc ?? it?.keyword_data?.cpc ?? 0;
    const comp = it.competition_index ?? it?.keyword_data?.competition_index ?? 0;
    const score = vol * 0.7 + (100 - comp) * 2 + cpc * 10;
    out.push({ keyword: kw, volume: vol, cpc, competition_index: comp, intent: 'unknown', score });
  }
}

out.sort((a,b)=> b.score - a.score);
return out.slice(0,10).map(x => ({ json: x }));

// ONE node: init _poll + extract task_id(s)
const items = $input.all().map(it => it.json || {});
const out = [];

for (const j of items) {
  // 1) —Å—Ç–∞—Ä—Ç–æ–≤—ñ –ø–∞—Ä–∞–º–µ—Ç—Ä–∏ –ø–æ–ª—ñ–Ω–≥—É –≤ payload
  const base = {
    ...j,
    _poll: { tries: 0, delayMs: 1500, maxTries: 10, maxDelayMs: 15000 },
  };

  // 2) –∑–±–∏—Ä–∞—î–º–æ –º–æ–∂–ª–∏–≤—ñ id –∑ –≤—ñ–¥–ø–æ–≤—ñ–¥—ñ DataForSEO
  const ids = [];
  if (Array.isArray(j.tasks)) {
    for (const t of j.tasks) {
      if (t?.id) ids.push(String(t.id));
      // –¥–æ–¥–∞—Ç–∫–æ–≤—ñ fallback'–∏, —è–∫—â–æ –∫–æ–ª–∏—Å—å –∑–º—ñ–Ω–∏—Ç—å—Å—è —Å—Ç—Ä—É–∫—Ç—É—Ä–∞
      if (Array.isArray(t.result)) {
        for (const r of t.result) {
          if (r?.id && !ids.includes(String(r.id))) ids.push(String(r.id));
          if (r?.task_id && !ids.includes(String(r.task_id))) ids.push(String(r.task_id));
        }
      }
    }
  }
  if (!ids.length && j.id) ids.push(String(j.id));
  if (!ids.length && j.task_id) ids.push(String(j.task_id));

  if (!ids.length) throw new Error('No task_id returned from DataForSEO');

  // 3) –≤—ñ–¥–¥–∞—î–º–æ –ø–æ –æ–¥–Ω–æ–º—É item –Ω–∞ –∫–æ–∂–µ–Ω task_id
  for (const id of ids) out.push({ json: { ...base, task_id: id } });
}

return out;

/**
 * TOKEN ECONOMY + –ö–†–ê–©–ï –í–Ü–î–û–ë–†–ê–ñ–ï–ù–ù–Ø –í UI
 * - —Ç—è–≥–Ω–µ–º–æ –¥–∞–Ω—ñ –∑ —ñ–Ω—à–∏—Ö –Ω–æ–¥ —á–µ—Ä–µ–∑ .first()
 * - –ø—ñ–¥—Ç—Ä–∏–º–∫–∞ —Ä—ñ–∑–Ω–∏—Ö –Ω–∞–∑–≤ –Ω–æ–¥ —Ç–∞ —à–ª—è—Ö—ñ–≤ (–≤ —Ç.—á. Get_an_information.body.*)
 * - –æ—Ü—ñ–Ω—é—î–º–æ —Ç–æ–∫–µ–Ω–∏ (~1 —Ç–æ–∫–µ–Ω ‚âà 4 —Å–∏–º–≤–æ–ª–∏), –æ–±—Ä—ñ–∑–∞—î–º–æ candidates –ø—ñ–¥ –±—é–¥–∂–µ—Ç
 * - –ø–æ–≤–µ—Ä—Ç–∞—î–º–æ —ñ –∫–æ—Ä–æ—Ç–∫—ñ –ø—Ä–µ–≤‚Äô—é—à–∫–∏ –¥–ª—è UI, —ñ –ø–æ–≤–Ω—ñ —Å—Ç—Ä—É–∫—Ç—É—Ä–∏ –¥–ª—è –ø–µ—Ä–µ–≤—ñ—Ä–∫–∏/–ø–∞–π–ø–ª–∞–π–Ω–∞
 */

// ====== –Ω–∞–ª–∞—à—Ç—É–≤–∞–Ω–Ω—è –ø—ñ–¥ —Å–≤–æ—é –º–æ–¥–µ–ª—å ======
const MODEL_LIMIT = 16000;          // –ª—ñ–º—ñ—Ç –∫–æ–Ω—Ç–µ–∫—Å—Ç—É –º–æ–¥–µ–ª—ñ
const RESERVED_FOR_RESPONSE = 2000; // –∑–∞–ø–∞—Å –Ω–∞ –≤—ñ–¥–ø–æ–≤—ñ–¥—å –º–æ–¥–µ–ª—ñ
const SAFETY_BUFFER = 300;          // —Å—Ç—Ä–∞—Ö–æ–≤–∏–π –±—É—Ñ–µ—Ä
const REQUEST_BUDGET = MODEL_LIMIT - RESERVED_FOR_RESPONSE - SAFETY_BUFFER;

// ====== —É—Ç–∏–ª—ñ—Ç–∏ ======
const toStr = (x) => (typeof x === 'string' ? x : JSON.stringify(x ?? '', null, 0));
const estTokens = (s) => Math.ceil(String(s || '').length / 4);
const preview = (s, max = 1200) => {
  const str = String(s ?? '');
  return str.length <= max ? str : (str.slice(0, max) + `‚Ä¶ [${str.length - max} more chars]`);
};

function safeParseJSON(text) {
  try { return JSON.parse(String(text)); } catch { return null; }
}

function fromNodeFirst(nodeName, path = []) {
  try {
    const n = $(nodeName).first();
    let v = n?.json;
    for (const k of path) v = v?.[k];
    return (v === undefined || v === null) ? '' : v;
  } catch { return ''; }
}

function firstHit(candidates) {
  for (const { node, path } of candidates) {
    const v = fromNodeFirst(node, path);
    if (v !== '' && v !== undefined && v !== null) return v;
  }
  return '';
}

function paginate(arr, pageSize = 50) {
  const pages = [];
  for (let i = 0; i < arr.length; i += pageSize) {
    pages.push(arr.slice(i, i + pageSize));
  }
  return pages;
}

// ====== –¥–∂–µ—Ä–µ–ª–∞ (–ø—ñ–¥–ª–∞—à—Ç—É–π –Ω–∞–∑–≤–∏ –ø—ñ–¥ —Å–≤–æ—ó –Ω–æ–¥–∏) ======
// ICP
const icpRaw = firstHit([
  // –∫–ª–∞—Å–∏—á–Ω—ñ –≤–∞—Ä—ñ–∞–Ω—Ç–∏
  { node: 'Get Client Input',      path: ['icp'] },
  { node: 'Get_strategie',         path: ['icp'] },
  // —Ç–≤—ñ–π –≤—É–∑–æ–ª Get_an_information (–º–æ–∂–µ –±—É—Ç–∏ –∑ body –∞–±–æ –±–µ–∑)
  { node: 'Get_an_information',    path: ['client_input', 'icp'] },
  { node: 'Get_an_information',    path: ['body', 'client_input', 'icp'] },
  { node: 'Get_an_information',    path: ['icp'] },
  { node: 'Get_an_information',    path: ['body', 'icp'] },
  // —ñ–Ω–∫–æ–ª–∏ –Ω–∞–∑–≤–∞ –∑ –ø—Ä–æ–±—ñ–ª–∞–º–∏
  { node: 'Get an information',    path: ['client_input', 'icp'] },
  { node: 'Get an information',    path: ['body', 'client_input', 'icp'] },
  { node: 'Get an information',    path: ['icp'] },
  { node: 'Get an information',    path: ['body', 'icp'] },
]);

// Strategy (—Ä—è–¥–æ–∫ –∞–±–æ –æ–±‚Äô—î–∫—Ç). –°–ø–µ—Ä—à—É —à—É–∫–∞—î–º–æ content.
const strategyRaw = firstHit([
  { node: 'Get_strategie',         path: ['content'] },
  { node: 'Get_strategie',         path: [] },
  { node: 'Get_an_information',    path: ['strategy', 'content'] },
  { node: 'Get_an_information',    path: ['body', 'strategy', 'content'] },
  { node: 'Get_an_information',    path: ['strategy'] },
  { node: 'Get_an_information',    path: ['body', 'strategy'] },
  { node: 'Get an information',    path: ['strategy', 'content'] },
  { node: 'Get an information',    path: ['body', 'strategy', 'content'] },
  { node: 'Get an information',    path: ['strategy'] },
  { node: 'Get an information',    path: ['body', 'strategy'] },
]);

// Keyword Prefs
const prefsObj = firstHit([
  { node: 'Extract Keyword Prefs', path: ['strategy_keyword_preferences'] },
  { node: 'Extract Keyword Prefs', path: [] },
]) || {};

// ====== –∫–∞–Ω–¥–∏–¥–∞—Ç–∏ –∑ –ø–æ—Ç–æ—á–Ω–æ–≥–æ item ======
const current = $json || {};
let candidatesInput =
  Array.isArray(current.candidates_array) ? current.candidates_array
  : Array.isArray(current.data) ? current.data
  : [];

// –º—ñ–Ω—ñ–º—ñ–∑—É—î–º–æ candidates
const minCandidates = candidatesInput.map(k => ({
  keyword: k.keyword,
  volume: Math.round(k.volume || 0),
  cpc: Math.round((k.cpc || 0) * 100) / 100,
  competition_index: Math.round(k.competition_index || 0),
  score: Math.round((k.score || 0) * 10) / 10
}));

// ====== —Å–µ—Ä—ñ–∞–ª—ñ–∑—É—î–º–æ –±–ª–æ–∫–∏ –¥–ª—è –æ—Ü—ñ–Ω–∫–∏ —Ç–æ–∫–µ–Ω—ñ–≤ ======
let icp_full  = toStr(icpRaw).trim();
let strategy  = toStr(strategyRaw).trim();
let prefsJson = JSON.stringify(prefsObj ?? {});
let candJson  = JSON.stringify(minCandidates ?? []);

// –æ—Ü—ñ–Ω–∫–∞ —Ç–æ–∫–µ–Ω—ñ–≤ –ø–æ –±–ª–æ–∫–∞—Ö
function tokensOfBlocks(obj) {
  const t = {};
  let sum = 0;
  for (const [k, v] of Object.entries(obj)) {
    t[k] = estTokens(v);
    sum += t[k];
  }
  return { t, sum };
}

let blocks = { icp_full, strategy, prefsJson, candJson };
let { t: tokens, sum: total_tokens } = tokensOfBlocks(blocks);

// —è–∫—â–æ –Ω–µ –≤–ª—ñ–∑–∞—î ‚Äî –æ–±—Ä—ñ–∑–∞—î–º–æ candidates (–¥–ª—è LLM-–∫–æ–Ω—Ç–µ–∫—Å—Ç—É)
if (total_tokens > REQUEST_BUDGET) {
  const arr = [...minCandidates];
  while (arr.length && total_tokens > REQUEST_BUDGET) {
    arr.pop(); // –ø–æ –æ–¥–Ω–æ–º—É –∑ —Ö–≤–æ—Å—Ç–∞
    candJson = JSON.stringify(arr);
    ({ t: tokens, sum: total_tokens } = tokensOfBlocks({ icp_full, strategy, prefsJson, candJson }));
  }
}

// ====== –ö–†–ê–©–ï –í–Ü–î–û–ë–†–ê–ñ–ï–ù–ù–Ø –í UI ======
// 1) –°–ø—Ä–æ–±–∞ —Ä–æ–∑–ø–∞—Ä—Å–∏—Ç–∏ —Å—Ç—Ä–∞—Ç–µ–≥—ñ—é —è–∫ JSON
const strategyParsed = safeParseJSON(strategy);

// 2) –ü–æ–≤–Ω—ñ –æ–±‚Äô—î–∫—Ç–∏ (–¥–ª—è –ª—é–¥–∏–Ω–∏/–¥–µ–±–∞–≥—É)
const icp_obj        = (typeof icpRaw === 'object') ? icpRaw : (safeParseJSON(icpRaw) ?? { text: String(icpRaw) });
const strategy_obj   = strategyParsed ?? (typeof strategyRaw === 'object' ? strategyRaw : { text: strategy });
const prefs_obj_full = prefsObj ?? {};
const candidates_min = safeParseJSON(candJson) ?? [];

// 3) –ü—Ä–µ–≤‚Äô—é—à–∫–∏ –¥–ª—è –∫–æ–º–ø–∞–∫—Ç–Ω–æ–≥–æ –ø–µ—Ä–µ–≥–ª—è–¥—É
const ui_preview = {
  icp: preview(icp_full),
  strategy: preview(strategy),
  keyword_prefs: preview(prefsJson),
  candidates_json: preview(candJson),
};

// 4) –ü–∞–≥—ñ–Ω–∞—Ü—ñ—è –∫–∞–Ω–¥–∏–¥–∞—Ç—ñ–≤
const candidates_pages = paginate(candidates_min, 50).map((page, idx) => ({
  page_index: idx,
  count: page.length,
  items: page
}));

// ====== –í–ò–•–Ü–î ======
return [{
  json: {
    // --- –î–∞–Ω—ñ –¥–ª—è LLM (—Ä—è–¥–∫–∏ –∑ —É—Ä–∞—Ö—É–≤–∞–Ω–Ω—è–º —Ç–æ–∫–µ–Ω-–µ–∫–æ–Ω–æ–º—ñ–∫–∏) ---
    icp_full,
    strategy,
    strategy_keyword_preferences_json: prefsJson,
    candidates_min_json: candJson,

    // --- –ü–æ–≤–Ω—ñ —Å—Ç—Ä—É–∫—Ç—É—Ä–∏ –¥–ª—è –ø–µ—Ä–µ–≥–ª—è–¥—É/–æ–±—Ä–æ–±–∫–∏ ---
    icp_obj,
    strategy_obj,
    prefs_obj_full,
    candidates_min,

    // --- –ü–∞–≥—ñ–Ω–∞—Ü—ñ—è –¥–ª—è UI ---
    candidates_pages_meta: {
      pages: candidates_pages.length,
      total_items: candidates_min.length,
      page_size: 50
    },
    candidates_pages,

    // --- –ü—Ä–µ–≤‚Äô—é—à–∫–∏ –¥–ª—è UI ---
    ui_preview,

    // --- –î—ñ–∞–≥–Ω–æ—Å—Ç–∏–∫–∞/–ª—ñ–º—ñ—Ç–∏ ---
    model_limit: MODEL_LIMIT,
    reserved_for_response: RESERVED_FOR_RESPONSE,
    safety_buffer: SAFETY_BUFFER,
    request_budget: REQUEST_BUDGET,
    tokens_by_block: tokens,
    total_tokens,
    fits_budget: total_tokens <= REQUEST_BUDGET
  }
}];

let text = $json.output ?? $json;
let obj;

if (typeof text !== 'string') {
  obj = text;
} else {
  try {
    // –≤–∏–¥–∞–ª—è—î–º–æ –º–æ–∂–ª–∏–≤—ñ —Ç—Ä—ñ–ø–ª-–±–µ–∫—Ç—ñ–∫–∏
    text = text.replace(/```json|```/g, '').trim();
    obj = JSON.parse(text);
  } catch (e) {
    return [{
      json: { error: 'LLM returned non-JSON', raw: text }
    }];
  }
}

// –æ—Ç—Ä–∏–º—É—î–º–æ best_keywords
const data = obj.best_keywords || [];
const keywords = data.map(k => k.keyword);

// –ø–æ–≤–µ—Ä—Ç–∞—î–º–æ —ñ –æ—Ä–∏–≥—ñ–Ω–∞–ª—å–Ω–∏–π –æ–±‚Äô—î–∫—Ç, —ñ keywords
return [{
  json: {
    ...obj,
    keywords
  }
}];

/**
 * ONE NODE = three previous nodes:
 * 1) Pick Global Best Keywords (–ø–æ –≤—Å—ñ—Ö –≤—Ö—ñ–¥–Ω–∏—Ö –∞–π—Ç–µ–º–∞—Ö)
 * 2) Prepare Candidates (candidates_json / candidates_array)
 * 3) Extract Keyword Preferences (flatten, no arrays) –∑ $('Get_strategie')
 *
 * Mode: Run Once for All Items
 */

// ===================== 1) Pick Global Best Keywords =====================
const MIN_VOLUME = 0;            // –º—ñ–Ω—ñ–º–∞–ª—å–Ω–∏–π volume
const MAX_COMP   = 90;           // –º–∞–∫—Å–∏–º—É–º competition_index
const TOP_N      = 5;            // —Å–∫—ñ–ª—å–∫–∏ —Ç–æ–ø–æ–≤–∏—Ö –ø–æ–≤–µ—Ä–Ω—É—Ç–∏ (–≤–∫–ª—é—á–Ω–æ –∑ best_one)
const EXCLUDE    = ["free", "cheap"]; // —Å—Ç–æ–ø-—Å–ª–æ–≤–∞

const INPUT = $input.all();
let pool = [];

// –∑–≥–æ—Ä—Ç–∞—î–º–æ –≤—Ö—ñ–¥ —É –ø–ª–∞—Å–∫–∏–π –º–∞—Å–∏–≤ –∫–∞–Ω–¥–∏–¥–∞—Ç—ñ–≤
for (const it of INPUT) {
  const j = it.json || {};
  if (Array.isArray(j.best_keywords)) {
    for (const k of j.best_keywords) {
      if (k && k.keyword) pool.push(k);
    }
  } else if (j.keyword) {
    pool.push(j);
  }
}

// –Ω–æ—Ä–º–∞–ª—ñ–∑–∞—Ü—ñ—è
pool = pool.map(k => ({
  keyword: String(k.keyword || '').trim(),
  volume: Number(k.volume || 0),
  cpc: Number(k.cpc || 0),
  competition_index: Number(k.competition_index || 0),
  score: Number(k.score || 0),
  reason: k.reason ? String(k.reason) : ''
})).filter(k => k.keyword);

// —Ñ—ñ–ª—å—Ç—Ä–∏
const excludeLC = EXCLUDE.map(s => s.toLowerCase());
pool = pool.filter(k => {
  if (k.volume < MIN_VOLUME) return false;
  if (k.competition_index > MAX_COMP) return false;
  const key = k.keyword.toLowerCase();
  if (excludeLC.some(term => key.includes(term))) return false;
  return true;
});

// –¥–µ–¥—É–ø + –≤–∏–±—ñ—Ä –∫—Ä–∞—â–æ–≥–æ
const pickBetter = (a, b) =>
  (a.score > b.score) ||
  (a.score === b.score && a.volume > b.volume) ||
  (a.score === b.score && a.volume === b.volume && a.competition_index < b.competition_index) ||
  (a.score === b.score && a.volume === b.volume && a.competition_index === b.competition_index && a.cpc < b.cpc);

const byText = new Map();
for (const k of pool) {
  const key = k.keyword.toLowerCase();
  const prev = byText.get(key);
  if (!prev || pickBetter(k, prev)) byText.set(key, k);
}
const unique = Array.from(byText.values());

// –≥–ª–æ–±–∞–ª—å–Ω–µ —Å–æ—Ä—Ç—É–≤–∞–Ω–Ω—è
unique.sort((a, b) =>
  (b.score - a.score) ||
  (b.volume - a.volume) ||
  (a.competition_index - b.competition_index) ||
  (a.cpc - b.cpc)
);

// —Ä–µ–∑—É–ª—å—Ç–∞—Ç —á–∞—Å—Ç–∏–Ω–∏ (1)
const best_keywords = unique.slice(0, Math.max(1, Math.min(TOP_N, unique.length)));
const best_one = best_keywords[0] || null;
const pickResult = {
  best_one,
  best_keywords,
  pool_size: unique.length,
  filtered_out: pool.length - unique.length,
  filters_used: {
    min_volume: MIN_VOLUME,
    max_competition_index: MAX_COMP,
    exclude_terms: EXCLUDE
  }
};

// ===================== 2) Prepare Candidates =====================
// –†–∞–Ω—ñ—à–µ —Ç–∏ —Ç—è–≥–Ω—É–≤ –∑ $json.data –ø—ñ—Å–ª—è Aggregate. –¢–µ–ø–µ—Ä Aggregate –Ω–µ–º–∞—î,
// —Ç–æ–º—É —Ä–æ–±–∏–º–æ —Å—É–º—ñ—Å–Ω–æ: —è–∫—â–æ —É –≤—Ö—ñ–¥–Ω–æ–º—É $json —î .data ‚Äî –≤–∏–∫–æ—Ä–∏—Å—Ç–∞—î–º–æ –π–æ–≥–æ;
// —ñ–Ω–∞–∫—à–µ –±–µ—Ä–µ–º–æ –Ω–∞—à –∑–≥–µ–Ω–µ—Ä–æ–≤–∞–Ω–∏–π pickResult.
// –ù–∞ –≤–∏—Ö–æ–¥—ñ –∑—Ä–æ–±–∏–º–æ —Ç–∞–∫ —Å–∞–º–æ: candidates_array —ñ candidates_json.

let candidatesSource;
try {
  // —è–∫—â–æ –ø–æ–ø–µ—Ä–µ–¥–Ω—ñ –Ω–æ–¥–∏ –≤—Å–µ –∂ –ø—ñ–¥–∫–ª–∞–ª–∏ data ‚Äî –≤—ñ–∑—å–º–µ–º–æ —ó—ó
  const j = ($json && typeof $json === 'object') ? $json : {};
  candidatesSource = j.data ?? j ?? pickResult;
} catch {
  candidatesSource = pickResult;
}

// –≥–∞—Ä–∞–Ω—Ç—É—î–º–æ –º–∞—Å–∏–≤
const candidates_array = Array.isArray(candidatesSource)
  ? candidatesSource
  : [candidatesSource];

const candidates_json = JSON.stringify(candidates_array, null, 2);

// ===================== 3) Extract Keyword Preferences =====================
function tryParseJSON(text) {
  try { return JSON.parse(text); } catch { return null; }
}
function stripCodeFences(s) {
  return String(s || '')
    .replace(/```json/gi, '')
    .replace(/```/g, '')
    .trim();
}
function extractObjWithKey(text, key) {
  const blocks = String(text).match(/\{[\s\S]*?\}/g);
  if (!blocks) return null;
  for (const b of blocks) {
    if (b.includes(`"${key}"`)) {
      const parsed = tryParseJSON(b);
      if (parsed) return parsed;
    }
  }
  return null;
}

let rawStrategy;
try { rawStrategy = $('Get_strategie').first().json.content; } catch { rawStrategy = undefined; }

let strategy = {};
if (rawStrategy && typeof rawStrategy === 'object') {
  strategy = rawStrategy;
} else if (typeof rawStrategy === 'string') {
  strategy = tryParseJSON(rawStrategy) || {};
  if (!Object.keys(strategy).length) {
    const stripped = stripCodeFences(rawStrategy);
    strategy = tryParseJSON(stripped) || strategy;
  }
  if (!Object.keys(strategy).length) {
    const extracted = extractObjWithKey(rawStrategy, 'keyword_preferences');
    if (extracted) strategy = extracted;
  }
}

let prefs = (strategy && strategy.keyword_preferences) || {};
if (!prefs || !Object.keys(prefs).length) {
  prefs = {
    required_categories: ["generic", "brand", "feature"],
    min_volume: 0,
    max_competition_index: 90,
    include_terms: ["oem", "grade a"],
    exclude_terms: ["free", "cheap"],
